import { View} from 'react-native';
import { Formik } from 'formik';
import * as yup from 'yup';

import Input from './Input'

// Validation-yup
const schema= yup.object({
  hours: yup.number().required("Must insert hours")
    .test("is hours valid", "Must be between 0 - 24", (val) => (parseInt(val) >= 0 && parseInt(val) < 24)),
  minute: yup.number().required("Must insert minutes")
  .test("is minutes valid", "Must be between 0 - 60", (val) => (parseInt(val) >= 0 && parseInt(val) < 60)),
  second: yup.number().required("Must insert seconds")
  .test("is seconds valid", "Must be between 0 - 60", (val) => (parseInt(val) >= 0 && parseInt(val) < 60)),
  date: yup.date().required("Must insert date"),
})

function ReviewForm(){
  return (
    <View>
      <Formik 
        validationSchema={schema}
        initialValues={{hours: "",minute:"",second:"", date:"" }} 
        onSubmit={(values,actions)=>{
          actions.resetForm();
          save(values)
          console.log(values)}}
      >
        {(props)=>(
          <Input props={props}></Input>
        )}
      </Formik>
   </View>
  )
}

export default ReviewForm;
