import { useState } from 'react';
import { StyleSheet, Text,View } from 'react-native';

import ButtonsRow from '../stopper/components/ButtonsRow';
import ButtonClick from '../stopper/components/ButtonClick'
import ModalInput from '../watch/components/ModalInput';


const set = ()=>{
    if(modalOpen == false){
      setmodalOpen(true)
    }else{
      setmodalOpen(false)
    }
  }

const Enable= ()=>{
    console.log(6)
}

const Disables= ()=>{
    console.log(7)
}

const save=(values)=>{
    let userTimeInSeconds = parseInt(values.hours) * 60 * 60 +
       parseInt(values.minute) * 60 +
       parseInt(values.second)
    
    let currTimeInSeconds = 
      clockNow.getHours() * 60 * 60 +
      clockNow.getMinutes() * 60 +
      clockNow.getSeconds()
    console.log(values.date)
    setclockOffset((userTimeInSeconds - currTimeInSeconds) * 1000)
    setdateOffset(new Date(values.date).getTime() + userTimeInSeconds * 1000 - clockNow.getTime())
  }

export default function AppAlarm() {

    const [alarm,setAlarm]= useState(0)
    const [modalOpen,setmodalOpen]=useState(false)

  return (
    <View>
        <Text>hi</Text>

        {alarm == 0 && (
        <ButtonsRow>
            <ButtonClick title="Set"  background="#5F9EA0" onPress={set}></ButtonClick>
            <ButtonClick title="Enable"  background="#5F9EA0" onPress={Enable}></ButtonClick>
        </ButtonsRow>
        )}

        {alarm == 0 && (
        <ButtonsRow>
            <ButtonClick title="Set"  background="#5F9EA0" onPress={set}></ButtonClick>
            <ButtonClick title="Disables"  background="#5F9EA0" onPress={Disables}></ButtonClick>
        </ButtonsRow>
        )}

        <ModalInput modalOpen={modalOpen} set={set} save={save}/>

        

    </View>
  );
}

const styles = StyleSheet.create({

});
