import {  StyleSheet, View, Text, TextInput, Button } from 'react-native';

import ErrorsAlarm from './ErrorsAlarm'

function InputAlarm({props}){
  return(
    <View>
        <View style={styles.inputRow}>
            <Text>Time</Text>
            <TextInput 
                style={styles.inputWatch} 
                onChangeText={props.handleChange('hours')} 
                placeholder='HH' 
                keyboardType="numeric" 
                value={props.values.hours}
                onBlur={props.handleBlur('hours')}
            />
            <TextInput 
            style={styles.inputWatch} 
            onChangeText={props.handleChange('minute')} 
            placeholder='MM' 
            keyboardType="numeric" 
            value={props.values.minute}
            onBlur={props.handleBlur('minute')}
            />   
        </View>

    
        <Button 
        style={styles.save} 
        title="Save" 
        onPress={props.handleSubmit} ></Button>
        <ErrorsAlarm props={props}></ErrorsAlarm>
    </View>
  )
}

const styles = StyleSheet.create({
    inputRow:{
        flexDirection:"row",
        marginTop:5
      },
      inputWatch:{
        width: 35,
        padding: 3,
        marginLeft:3,
        borderWidth:2,
        borderColor:'black',
      },
      save:{
        marginVertical:10,
        backgroundColor: "red",
      },
});

export default InputAlarm;
