import React, { useState } from 'react';
import { StyleSheet, Text, View } from 'react-native';
import Timer from './components/Timer'
import RoundButton from './components/RoundButton'
import ButtonsRow from './components/ButtonsRow'


const AppWatch= ()=>{
const [startTime, setStartTime]= useState(0)
const [currTime, setCurrTime]= useState(0)
const [lastLap, setlastLap]= useState(0)
const [timer, setTimer]= useState(0)

const start = ()=>{
  const time = new Date().getTime()
  setStartTime(time)
  setCurrTime(time)

  setTimer(
    setInterval(()=>{
      setCurrTime(new Date().getTime())
    },100)
  ) 
}

const stop = ()=>{
  const time = new Date().getTime()
  clearInterval(timer)
  setlastLap(lastLap + time - startTime)
  setStartTime(0)
  setCurrTime(0)
}

const reset = ()=>{
  setStartTime(0)
  setCurrTime(0)
  setlastLap(0)
}

const resume = ()=>{
  const time= new Data().getTime();
  setStartTime(time)
  setCurrTime(time)
  setTimer(
    setInterval(()=>{
      setCurrTime(new Date().getTime())
    },100)
  )
  }

  return (
    <View style={styles.container}>
      <Timer interval={lastLap + currTime - startTime} style={styles.timer} ></Timer>
      <Timer interval={lastLap} style={styles.mimi} ></Timer>
      
      {startTime == 0 && (
        <ButtonsRow>
        <RoundButton title="Start" color="green" background="red" onPress={start}></RoundButton>
        <RoundButton title="Rest" color="green" background="red" onPress={reset}></RoundButton>
      </ButtonsRow>
      )}

      {startTime > 0 && (
      <ButtonsRow>
        <RoundButton title="Stop" color="pink" background="blue"  onPress={stop}></RoundButton>
        <RoundButton title="Rest" color="green" background="red" onPress={reset}></RoundButton>
      </ButtonsRow>
      )}

    </View>
 );
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'black',
    alignItems: 'center',
    paddingTop: 130,
    paddingHorizontal:20
  },
  timer:{
    color:'white',
    fontSize: 76,
    fontWeight:'200',

  },
  mimi:{
    color:'white'
  }
});

export default AppWatch
