import React, { Component } from 'react';
import {  StyleSheet, View } from 'react-native';
import Timer from './components/Timer'
import RoundButton from './components/RoundButton'
import LapListTable from './components/LapListTable'
import ButtonsRow from './components/ButtonsRow'

 class AppWatch extends Component {
  constructor(props){
    super(props)
    this.state={
      start:0,
      now: 0,
      laps: []
    }
  }

start= ()=>{
  const now = new Date().getTime()
  this.setState({
    start:now,
    now,
    laps:[0],
  })
  this.timer= setInterval(()=>{
    this.setState({
      now: new Date().getTime()})
  },100)
}

lap = () => {
  const timesTamp = new Data().getTime() 
  const {laps, now, start}= this.state
  const [firstLap, ...other] = laps
  this.setState({
    laps: [0,firstLap + now - start, ...other],
    start:timesTamp,
    now: timesTamp
  })
}

stop = ()=>{
  clearInterval(this.timer)
  const {laps, now, start}= this.state
  const [firstLap, ...other] = laps
  this.setState({
    laps: [firstLap + now - start, ...other],
    start:0,
    now: 0,
  })
}

reset = () => {
  this.setState({
    laps:[],
    start:0,
    now:0
  })
}

resume = () => {
  const now= new Data().getTime();
  this.setState({
    start: now,
    now
  })
  this.timer= setInterval(()=>{
    this.setState({
      now: new Date().getTime()})
  },100)
}


  render(){
    const {laps,start,now}=this.state;
    const timer = now-start;
    return (
      <View style={styles.container}>
        <Timer interval={laps.reduce((total,curr) => total+curr, 0)+timer} style={styles.timer} ></Timer>
        {laps.length == 0 && (
          <ButtonsRow>
          <RoundButton title="Start" color="green" background="red" onPress={this.start}></RoundButton>
          <RoundButton title="Lap" color="pink" background="blue" disable></RoundButton>
        </ButtonsRow>
        )}
        {start > 0 && (
        <ButtonsRow>
          <RoundButton title="Stop" color="pink" background="blue"  onPress={this.stop}></RoundButton>
          <RoundButton title="Lap" color="green" background="red" onPress={this.lap}></RoundButton>
        </ButtonsRow>
        )}
        {laps.length > 0 && start == 0 && (
          <ButtonsRow>
            <RoundButton title="Rest" color="green" background="red" onPress={this.reset}></RoundButton>
            <RoundButton title="Start" color="pink" background="blue"  onPress={this.resume}></RoundButton>
          </ButtonsRow>
        )}
        <LapListTable laps={laps} timer={timer} ></LapListTable>
      </View>
   );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'black',
    alignItems: 'center',
    paddingTop: 130,
    paddingHorizontal:20
  },
  timer:{
    color:'white',
    fontSize: 76,
    fontWeight:'200',
    width: 120,
  },
});
