import React,{ useEffect, useState } from 'react';
import { StyleSheet, Text,View } from 'react-native';
// var Sound = require('react-native-sound');

// import AlarmMp3 from './components/Alarm.mp3'
import ButtonsRow from '../stopper/components/ButtonsRow';
import ButtonClick from '../stopper/components/ButtonClick';
import ModalInput from '../utills/components/ModalInput';
import TimeAlarm from './components/TimeAlarm';





export default function AppAlarm() {
    const [enable,setEnable] = useState(false)
    const [hoursValue,sethoursValue] = useState()
    const [minutesValue,setminutesValu] = useState()

    const [modalOpen,setmodalOpen] = useState(false)
    const [modalFormat, setmodalFormat] = useState(true)

    const checkAlarm = ()=>{
        let timrCurr= new Date();
        let hourCurr=timrCurr.getHours()
        let minutesCurr= timrCurr.getMinutes()

        if(hoursValue== hourCurr && minutesValue == minutesCurr){
            console.log('לשים שיפעל הסאונד')
        }
           console.log(hourCurr,minutesCurr)
           console.log(hoursValue,minutesValue)
    }

    useEffect(()=>{
        setInterval(()=>{
            checkAlarm()
        },1000)
    },[])

    const set = ()=>{
        if(modalOpen == false){
            setmodalOpen(true)
        }else{
            setmodalOpen(false)
        }
    }
    
    const Enable= ()=>{
        console.log('לשים שיפעל הסאונד')
        setEnable(true)
    }
    
    const Disables= ()=>{
        console.log('לשים שיפסק הסאונד')
        setEnable(false)
    }
    
    const save=(values)=>{
        let hoursNumber=parseInt(values.hours)
        let minuteNumber= parseInt(values.minute)
        sethoursValue(hoursNumber)
        setminutesValu(minuteNumber)
        console.log()

        // checkAlarm()
      }




  return (
    <View style={styles.container}>
        <TimeAlarm ></TimeAlarm>
        

        {enable == false && (
        <ButtonsRow>
            <ButtonClick title="Set"  background="#5F9EA0" onPress={set} fontSize={22}></ButtonClick>
            <ButtonClick title="Enable"  background="#5F9EA0" onPress={Enable} fontSize={17}></ButtonClick>
        </ButtonsRow>
        )}

        {enable != false && (
        <ButtonsRow>
            <ButtonClick title="Set"  background="#5F9EA0" onPress={set} fontSize={22}></ButtonClick>
            <ButtonClick title="Disables"  background="#5F9EA0" onPress={Disables} fontSize={14}></ButtonClick>
        </ButtonsRow>
        )}

        <ModalInput modalOpen={modalOpen} set={set} save={save} modalFormat={modalFormat}/>

        

    </View>
  );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: 'black',
        alignItems: 'center',
        paddingTop: 130,
      },
  
});
