import React, { useState, useEffect } from 'react';
import { StyleSheet, View} from 'react-native';
import moment from "moment";
import AsyncStorage from '@react-native-async-storage/async-storage';

import ButtonsRow from '../utills/components/ButtonsRow'
import ButtonClick from '../utills/components/ButtonClick'
import DisplayDateClock from './components/DisplayDateClock'
import ModalInput from '../utills/components/ModalInput';


 const AppWatch = ()=> {
   const clockNow=new Date()
   
    const [fotmetWatch,setfotmetWatch]= useState('24H')

    const [clock,setclock]=useState()
    const [clockFormat,setclockFormat]=useState('en-US')


    // Modal
    const [modalOpen,setmodalOpen]=useState(false)
    const [modalFormat, setmodalFormat] = useState(false)

    const retrieveData = async () => {
      try {
        const clock = await AsyncStorage.getItem('clockOffset');
        if (clock !== null) {
          setclockOffset(parseInt(clock))
        }
        const date = await AsyncStorage.getItem('dateOffset');
        if (date !== null) {
          setdateOffset(parseInt(date))
        }
        console.log("galdebug")
      } catch (error) {
        console.log(error)
      }
    };

    retrieveData()

    const storeData = async () => {
      try {
        await AsyncStorage.setItem(
          'clockOffset',
          clockOffset
        );
        await AsyncStorage.setItem(
          'dateOffset',
          dateOffset
        );
      } catch (error) {
        console.log(error)
      }
    };

    useEffect(()=>{
        const interval =setInterval(()=>{
          setclock(clockNow.toLocaleTimeString())
        },1000)

        return ()=>{
          clearInterval(interval)
        }
    })

  // Functions for the buttons
    const set = ()=>{
      if(modalOpen == false){
        setmodalOpen(true)
      }else{
        setmodalOpen(false)
      }
    }

    const save=(values)=>{
      let userTimeInSeconds = parseInt(values.hours) * 60 * 60 +
         parseInt(values.minute) * 60 +
         parseInt(values.second)
      
      let currTimeInSeconds = 
        clockNow.getHours() * 60 * 60 +
        clockNow.getMinutes() * 60 +
        clockNow.getSeconds()
      console.log(values.date)
      setclockOffset((userTimeInSeconds - currTimeInSeconds) * 1000)
      setdateOffset(new Date(values.date).getTime() + userTimeInSeconds * 1000 - clockNow.getTime())
      storeData()
    }

    const pormatWatch = ()=>{
      if (clockFormat == 'fr-FR') {
        setclockFormat('en-US')
      } else {
        setclockFormat('fr-FR')
      }  
    }

    return (
      <View style={styles.container} >

        <DisplayDateClock 
          clock={new Date(clockNow.getTime() + clockOffset).toLocaleTimeString(clockFormat)}
          date={moment(new Date(new Date().getTime() + dateOffset)).format('DD/MM/YYYY')}
          fontSize={45}>
        </DisplayDateClock>
      
        {fotmetWatch == 'AM/PM' && (<ButtonsRow>
            <ButtonClick 
              title="Set"  
              background="#5F9EA0"  
              onPress={set} 
              fontSize={22}>
            </ButtonClick>

            <ButtonClick 
              title="AM/PM"  
              background="#5F9EA0" 
              onPress={pormatWatch} 
              fontSize={15}>
            </ButtonClick>
        </ButtonsRow>
        )}

        {fotmetWatch == '24H' && (<ButtonsRow>
            <ButtonClick 
            title="Set"  
            background="#5F9EA0"   
            onPress={set} 
            fontSize={22}>
            </ButtonClick>

            <ButtonClick 
            title="24H"  
            background="#B0E0E6" 
            onPress={pormatWatch} 
            fontSize={25}>
            </ButtonClick>
        </ButtonsRow>
        )}

        <ModalInput 
        modalOpen={modalOpen} 
        set={set} save={save} 
        modalFormat={modalFormat}
        />

      </View>
   );

}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: 'black',
        alignItems: 'center',
        paddingTop: 100,
        paddingHorizontal:20,
      },
    
});

export default AppWatch
