import React, { useState, useEffect } from 'react';
import {  StyleSheet, View, Modal, Button, TextInput, Text} from 'react-native';

import ButtonsRow from './components/ButtonsRow'
import ButtonClick from './components/ButtonClick'
import DisplayDateClock from './components/DisplayDateClock'


  let hour = new Date().getHours();
  let minutes = new Date().getMinutes();
  let seconds = new Date().getSeconds();
  let am_pm = 'pm';

const getCurrentTime = () => {
  if (minutes < 10) {
    minutes = '0' + minutes;
  }
  if (seconds < 10) {
    seconds = '0' + seconds;
  }
  if (hour > 12) {
    hour = hour - 12;
  }
  if (hour == 0) {
    hour = 12;
  }
  if (new Date().getHours() < 12) {
    am_pm = 'am';
  }
}

 const AppWatch = ()=> {
    const [fotmetWatch,setfotmetWatch]= useState('AM/PM')
    const [hh,sethhclock] = useState()
    const [mm,setmmclock] = useState()
    const [ss,setssclock] = useState()
    const [date,setdate] = useState()
    const[clock,setclock]=useState()

    const [hoursValue,sethoursValue]= useState('')
    const [minuteValue,setminuteValue]= useState('')
    const [secondValue,setsecondValue]= useState('')
    const [dateValue,setDateValue]= useState('')

    const [modalOpen,setmodalOpen]=useState(false)

    const clockNow=new Date('1995-12-17T03:24:00')

    useEffect(()=>{
        const interval =setInterval(()=>{
          getCurrentTime()
          sethhclock(hour)
          setmmclock(minutes)
          setssclock(seconds)
          setclock(clockNow.toLocaleTimeString())
            setdate(clockNow.toLocaleDateString())
        },1000)

        return ()=>{
          clearInterval(interval)
        }
    })

    const set = ()=>{
      if(modalOpen == false){
        setmodalOpen(true)
      }else{
        setmodalOpen(false)
      }
    }

    const chekValid= ()=>{
      if(hoursValue ==''){
        return flase
      }else if (minuteValue == ''){
        return false
      }else if(secondValue == ""){
        return false
      }else if(date == ""){
        return false
      }

      return true;
    }

    const save= ()=>{
      if(chekValid()){
        clockNow.setHours(Number(hoursValue), Number(minuteValue), Number(secondValue))
        console.log(+hoursValue)
        console.log(+minuteValue)
        console.log(+secondValue)
        setmodalOpen(false)
     }
    }

    const pormatWatch = ()=>{
      if (fotmetWatch == 'AM/PM') {
        setfotmetWatch('24H')
        setclock(clockNow.toLocaleTimeString('en-US'))
      } else {
        setfotmetWatch('AM/PM')
        setclock(clockNow.toLocaleTimeString('fr-FR'))
      }  
    }

    return (
      <View style={styles.container}>

        <DisplayDateClock clock={clock} date={date}></DisplayDateClock>
      
        {fotmetWatch == 'AM/PM' && (<ButtonsRow>
            <ButtonClick title="Set"  background="#5F9EA0"  onPress={set}></ButtonClick>
            <ButtonClick title="AM/PM"  background="#5F9EA0" onPress={pormatWatch}></ButtonClick>
        </ButtonsRow>
        )}

        {fotmetWatch == '24H' && (<ButtonsRow>
            <ButtonClick title="Set"  background="#5F9EA0"   onPress={set}></ButtonClick>
            <ButtonClick title="24H"  background="#B0E0E6" onPress={pormatWatch}></ButtonClick>
        </ButtonsRow>
        )}

        <Modal visible={modalOpen} animationType ={'slide'}  transparent={true}>
          <View style={styles.allSetModal}>
            <View style={styles.inputRow}>
                <Text>Time</Text>
                <TextInput style={styles.inputWatch} onChangeText={sethoursValue} placeholder='HH' keyboardType="numeric"/>
                <TextInput style={styles.inputWatch} onChangeText={setminuteValue} placeholder='MM' keyboardType="numeric"/>
                <TextInput style={styles.inputWatch} onChangeText={setsecondValue} placeholder='SS' keyboardType="numeric"/>
            </View>
            <View style={styles.inputRow}>
              <Text>Date</Text>
              <TextInput style={styles.inputDate}  onChange={setDateValue} placeholder='09/01/2021'/>
              <Button style={styles.save} title="Save" onPress={save} color="black"></Button>
            </View>
          </View>
        </Modal>
    
      </View>
   );

}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: 'black',
        alignItems: 'center',
        paddingTop: 100,
        paddingHorizontal:20
      },
      allSetModal:{
      margin: 20,
      backgroundColor: "white",
      borderRadius: 40,
      padding: 35,
      width: 200,
      justifyContent:'center'
      },
      inputRow:{
        flexDirection:"row",
        marginTop:5
        
      },
      inputWatch:{
        width: 35,
        padding: 3,
        marginLeft:3,
        borderWidth:2,
        borderColor:'black',
      },
      inputDate:{
        width: 100,
        height: 50,
        borderBottomColor:'black',
        borderWidth:2,
        marginLeft:3,
        padding: 10,
      },
      save:{
        color:'res'
      }
      
    
});

export default AppWatch
