import {  StyleSheet, View, Modal, TouchableWithoutFeedback, Keyboard} from 'react-native';
import { AntDesign } from '@expo/vector-icons'; 
import ReviewForm from './ReviewForm'


function ModalInput({modalOpen,set,save}){
    return(
        <Modal visible={modalOpen} animationType ={'slide'}  transparent={true}>
       
        <View style={styles.allSetModal}>
        <AntDesign name="closecircle" size={24} color="black" onPress={()=>{set()}}/>
          <ReviewForm save={save}></ReviewForm>
        </View>
       
      </Modal>
    )
}

const styles = StyleSheet.create({
    allSetModal:{
        margin: 20,
        backgroundColor: "white",
        borderRadius: 40,
        padding: 35,
        width: 200,
        justifyContent:'center'
        },
        inputRow:{
          flexDirection:"row",
          marginTop:5
          
        },
        inputWatch:{
          width: 35,
          padding: 3,
          marginLeft:3,
          borderWidth:2,
          borderColor:'black',
        },
        inputDate:{
          width: 100,
          height: 35,
          borderBottomColor:'black',
          borderBottomStartRadius:2,
          borderBottomWidth:2,
          marginLeft:3,
          padding: 10,
        },
        save:{
          marginVertical:10,
          backgroundColor: "red",
        }
});

export default ModalInput;
