import {  StyleSheet, View, TextInput, Text, Button } from 'react-native';
import { Formik } from 'formik';

function ReviewFrom(){
  return(
    <View>
        <Formik initialValues={{hours:"", minute:"" , second:"", date: ""  }}
        onSubmit={(value)=>{console.log(value)}}>
            {(props)=>{
                <View>
                    <View style={styles.inputRow}>
                        <Text>Time</Text>
                        <TextInput style={styles.inputWatch} onChangeText={props.handleChange('hours')} placeholder='HH' keyboardType="numeric" value={props.values.hours}/>
                        <TextInput style={styles.inputWatch} onChangeText={props.handleChange('minute')} placeholder='MM' keyboardType="numeric" value={props.values.minute}/>
                        <TextInput style={styles.inputWatch} onChangeText={props.handleChange('second')} placeholder='SS' keyboardType="numeric" value={props.values.second}/>
                    </View>
                    <View style={styles.inputRow}>
                        <Text>Date</Text>
                        <TextInput style={styles.inputDate}  onChange={props.handleChange('date')} placeholder='09/01/2021' value={props.values.date}/>
                    </View>
                        <Button style={styles.save} title="Save" onPress={props.handleSubmit} ></Button>
                </View>
            }}
        </Formik>
    </View>
  )
}

const styles = StyleSheet.create({
    inputRow:{
        flexDirection:"row",
        marginTop:5
        
      },
      inputWatch:{
        width: 35,
        padding: 3,
        marginLeft:3,
        borderWidth:2,
        borderColor:'black',
      },
      inputDate:{
        width: 100,
        height: 35,
        borderBottomColor:'black',
        borderBottomStartRadius:2,
        borderBottomWidth:2,
        marginLeft:3,
        padding: 10,
      },
      save:{
        marginVertical:10,
        backgroundColor: "red",
      }
});


export default ReviewFrom;
