import React, { useState,useEffect } from 'react';
import {  StyleSheet, View ,Text,TouchableOpacity,Modal, Button,TextInput, Dimensions} from 'react-native';


const WIDTH= Dimensions.get('window').width

function ButtonsRow({children}){
    return(
      <View style={styles.buttonsRow}>{children}</View>
    )
  }

  function RoundButton({title, color, background,onPress,disable}){
    return (
      <TouchableOpacity onPress={()=> !disable && onPress() } style={[styles.button,{backgroundColor: background}]} activeOpacity={ disable ? 1.0: 0.5}>
        <View style={styles.buttonBorder}>
          <Text style={[styles.buttonTitle,{color}]}>{title}</Text>
        </View>
      </TouchableOpacity>
    )
  }

  let hour = new Date().getHours();
  let minutes = new Date().getMinutes();
  let seconds = new Date().getSeconds();
  let am_pm = 'pm';

const getCurrentTime = () => {

  if (minutes < 10) {
    minutes = '0' + minutes;
  }

  if (seconds < 10) {
    seconds = '0' + seconds;
  }

  if (hour > 12) {
    hour = hour - 12;
  }

  if (hour == 0) {
    hour = 12;
  }

  if (new Date().getHours() < 12) {
    am_pm = 'am';
  }

}

 const AppDigitalWatch = ()=> {
    const [fotmetWatch,setfotmetWatch]= useState('AM/PM')
    const [hh,sethhclock] = useState()
    const [mm,setmmclock] = useState()
    const [ss,setssclock] = useState()
    const [date,setdate] = useState()
    const[clock,setclock]=useState()

    const [hoursValue,sethoursValue]= useState('')
    const [minuteValue,setminuteValue]= useState('')
    const [secondValue,setsecondValue]= useState('')
    const [dateValue,setDateValue]= useState('')

    const [modal,setModal]=useState(false)

    const clockNow=new Date()
    clockNow.setHours(10,10,10)

    useEffect(()=>{
        setInterval(()=>{
          getCurrentTime()
          sethhclock(hour)
          setmmclock(minutes)
          setssclock(seconds)
          setclock(clockNow.toLocaleTimeString())
            setdate(clockNow.toLocaleDateString())
        },1000)
        
    })

    const set = ()=>{
      if(modal == false){
        setModal(true)
      }else{
        setModal(false)
      }
    }

    const chekValid= ()=>{
      if(hoursValue ==''){
        return flase
      }else if (minuteValue == ''){
        return false
      }else if(secondValue == ""){
        return false
      }else if(date == ""){
        return false
      }

      return true;
    }

    const save= ()=>{
     if(chekValid()){
        clockNow.setHours(Number(hoursValue), Number(minuteValue), Number(secondValue))
        console.log(+hoursValue)
        console.log(+minuteValue)
        console.log(+secondValue)
        console.log('good')
     

     }
    }

    const pormatWatch = ()=>{
      if (fotmetWatch == 'AM/PM') {
        setfotmetWatch('24H')
        setclock(clockNow.toLocaleTimeString('en-US'))
      } else {
        setfotmetWatch('AM/PM')
        setclock(clockNow.toLocaleTimeString('fr-FR'))
      }  
    }

    return (
      <View style={styles.container}>
        <Text style={styles.data}> {clock}</Text>
        <Text style={styles.data}>{date}</Text>

        {fotmetWatch == 'AM/PM' && (<ButtonsRow>
            <RoundButton title="Set" color="green" background="red" onPress={set}></RoundButton>
            <RoundButton title="AM/PM" color="pink" background="blue"  onPress={pormatWatch}></RoundButton>
        </ButtonsRow>
        )}

        {fotmetWatch == '24H' && (<ButtonsRow>
            <RoundButton title="Set" color="green" background="red" onPress={set}></RoundButton>
            <RoundButton title="24H" color="pink" background="blue"  onPress={pormatWatch}></RoundButton>
        </ButtonsRow>
        )}

        <Modal visible={modal} animationType ={'fade'} style={styles.modal} transparent={true}>
          <View style={styles.allSet}>
              <TextInput style={styles.clock} onChangeText={sethoursValue} placeholder='HH' keyboardType="numeric"/>
              <TextInput style={styles.clock} onChangeText={setminuteValue} placeholder='MM' keyboardType="numeric"/>
              <TextInput style={styles.clock} onChangeText={setsecondValue} placeholder='SS' keyboardType="numeric"/>
              <TextInput  onChange={setDateValue} placeholder='09/01/2021'/>
              <Button title="Save" onPress={save}></Button>
          </View>
        </Modal>
    
      </View>
   );

}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: 'black',
        alignItems: 'center',
        paddingTop: 100,
        paddingHorizontal:20
      },
      data:{
        color:'white',
        fontSize: 15,
        fontWeight:'200',
        width: 100,
      },
      buttonsRow:{
        flexDirection:'row',
        alignSelf:'stretch',
        justifyContent:'space-between',
        marginTop:80,
        marginBottom:30
      },
      button:{
        width: 80,
        height: 80,
        borderRadius:40,
        justifyContent:'center',
        alignContent:'center'
      },
      buttonTitle:{
        fontSize:18,
      },
      buttonBorder:{
        width: 76,
        height: 76,
        borderRadius:38,
        borderWidth:1,
        justifyContent:'center',
        alignContent:'center'
      },
      modal:{
        height: 150,
        width: WIDTH-80,
        paddingTop:10,
        backgroundColor:'red',
        borderRadius: 10
      },
      
    

    
});

export default AppDigitalWatch
