import React,{ useEffect, useState } from 'react';
import { StyleSheet, Text,View } from 'react-native';
// var Sound = require('react-native-sound');


// import AlarmMp3 from './components/Alarm.mp3'

import ButtonsRow from '../stopper/components/ButtonsRow';
import ButtonClick from '../stopper/components/ButtonClick'
import ModalInput from '../utills/components/ModalInput';





export default function AppAlarm() {
    const [alarm,setalarm] = useState(0)
    const [hoursAlarm,sethoursAlarm] = useState("")
    const [minutesAlarm,setminutesAlarm] = useState("")

    const [modalOpen,setmodalOpen] = useState(false)
    const [modalFormat, setmodalFormat] = useState(true)

    const checkAlarm = ()=>{
        let timrCurr= new Date();
        let hourCurr=timrCurr.getHours()
        let minutesCurr= timrCurr.getMinutes()

        // if(hoursAlarm == hourCurr && minutesAlarm == minutesAlarm){
        //     console.log('לשים שיפעל הסאונד')
        // }
        console.log(hourCurr,minutesCurr)  
    }

    useEffect(()=>{
        setInterval(()=>{
            checkAlarm()
        },1000)
    },[])

    const set = ()=>{
        if(modalOpen == false){
            setmodalOpen(true)
        }else{
            setmodalOpen(false)
        }
    }
    
    const Enable= ()=>{
        console.log('לשים שיפעל הסאונד')
    }
    
    const Disables= ()=>{
        console.log('לשים שיפסק הסאונד')
    }
    
    const save=(values)=>{
        console.log(values)
      }




  return (
    <View style={styles.container}>
        <Text style={styles.time}>00:00</Text>

        {alarm == 0 && (
        <ButtonsRow>
            <ButtonClick title="Set"  background="#5F9EA0" onPress={set} fontSize={22}></ButtonClick>
            <ButtonClick title="Enable"  background="#5F9EA0" onPress={Enable} fontSize={17}></ButtonClick>
        </ButtonsRow>
        )}

        {alarm == 0 && (
        <ButtonsRow>
            <ButtonClick title="Set"  background="#5F9EA0" onPress={set} fontSize={22}></ButtonClick>
            <ButtonClick title="Disables"  background="#5F9EA0" onPress={Disables} fontSize={14}></ButtonClick>
        </ButtonsRow>
        )}

        <ModalInput modalOpen={modalOpen} set={set} save={save} modalFormat={modalFormat}/>

        

    </View>
  );
}

const styles = StyleSheet.create({
    time:{
        color: 'white',
        fontSize:45,
        backgroundColor:'pink',
        alignContent:'center',
        textAlign:'center',
        height: '50%',
        alignItems: 'center',
        flex: 1
        

        
    }
});
