import React, { useState } from 'react';
import { StyleSheet, View } from 'react-native';

import Timer from './components/Timer'
import RoundButton from './components/RoundButton'
import ButtonsRow from './components/ButtonsRow'


const Stopwatch= ()=>{

  const [startTime, setStartTime]= useState(0)
  const [currTime, setCurrTime]= useState(0)
  const [lastLap, setlastLap]= useState(0)
  const [timer, setTimer]= useState(0)


  // Functions for the buttons
  const start = ()=>{
    const time = new Date().getTime()
    setStartTime(time)
    setCurrTime(time)

    setTimer(
      setInterval(()=>{
        setCurrTime(new Date().getTime())
      },100)
    ) 
  }

  const stop = ()=>{
    const time = new Date().getTime()
    clearInterval(timer)
    setlastLap(lastLap + time - startTime)
    setStartTime(0)
    setCurrTime(0)
  }

  const reset = ()=>{
    setStartTime(0)
    setCurrTime(0)
    setlastLap(0)
  }


    return (
      <View style={styles.container}>
      
        <Timer interval={lastLap + currTime - startTime} style={styles.bigTimer} ></Timer>
        <Timer interval={lastLap} style={styles.smallTimer}></Timer>
        
        {startTime == 0 && (
          <ButtonsRow>
          <RoundButton title="Start"  background="#5F9EA0" onPress={start}></RoundButton>
          <RoundButton title="Rest"  background="#5F9EA0" onPress={reset}></RoundButton>
        </ButtonsRow>
        )}

        {startTime > 0 && (
        <ButtonsRow>
          <RoundButton title="Stop"  background="#B0E0E6"  onPress={stop}></RoundButton>
          <RoundButton title="Rest"  background="red" onPress={reset}></RoundButton>
        </ButtonsRow>
        )}

      </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'black',
    alignItems: 'center',
    paddingTop: 130,
  },
  bigTimer:{
    color:'white',
    fontSize: 55,
    fontWeight:'100',
    width: '50',
  },
  smallTimer:{
    color:'white',
    fontSize: 20,
    width: '25',
    fontWeight:'300',
  }
});

export default Stopwatch
